import React from 'react'
import {useState} from 'react'



import './map.scss'

import {GoogleMap, LoadScript, Marker, useGoogleMap, useLoadScript} from '@react-google-maps/api';
import PolygonMkad from "../PolygonMkad/Polygon";

const containerStyle = {
    minWidth: '100vh',
    height: '100vh'
}


function MyMapComponent() {

    const [ myMap, setMyMap ] = useState(null);
    const [center, setCenter] = useState({lat: 55.7522200, lng: 37.6155600});
    const [zoom,setZoom] = useState(10);

    const { isLoaded } = useLoadScript({
        googleMapsApiKey: "AIzaSyBYEey3dAT7X6YcANl4oNHBa5V-4KS2cGA",
    });

    const [ id, setId ] = useState(0);
    const [ markers, setMarkers ] = useState([]);
    const [ drawMarker, setDrawMarker ] = useState(false);
    //
    // const addMarker = (coords) => {
    //     setId((id)=>id+1);
    //     console.log(id)
    //     console.log(markers)
    //     setMarkers((markers) => markers.concat([{coords, id}]))
    //
    //
    // }

    let coords

    const renderMap = () => (
        <div>
            <GoogleMap
                center={center}
                zoom={zoom}
                mapContainerStyle={containerStyle}
                onLoad={map => setMyMap(map)}
                onClick={(e)=> {
                    coords = e.latLng.toJSON()
                    setDrawMarker(()=>!drawMarker);
                    console.log(coords)

                    setMarkers((markers) => drawMarker ?
                        new google.maps.marker({
                               position: coords,
                       }

                       )
                        : null)
                 }
                }

            >
                <PolygonMkad/>
                {(drawMarker === true) ? (
                            <Marker
                                position={coords}
                            />)
                 : null }
            </GoogleMap>
        </div>
)

    console.log(drawMarker)


    return isLoaded ? renderMap() : null;

}



export default MyMapComponent
