import React from 'react'
import {useState} from 'react'



import './map.scss'

import {GoogleMap, LoadScript, Marker, useLoadScript} from '@react-google-maps/api';
import PolygonMkad from "../PolygonMkad/Polygon";

const containerStyle = {
    minWidth: '100vh',
    height: '100vh'
}




function MyMapComponent() {

    const [center, setCenter] = useState({ lat: 55.7522200, lng: 37.6155600, });
    const [zoom,setZoom] = useState(10);

    const [ id, setId ] = useState(0);
    const [ markers, setMarkers ] = useState([]);
    const [ drawMarker, setDrawMarker ] = useState(false);


    const addMarker = (coords) => {


        setId((id)=>id+1);
        setMarkers((markers) => markers.concat([{coords, id}]) )
    }


    return  (
        <div className='map'>
            <LoadScript
                googleMapsApiKey="AIzaSyBYEey3dAT7X6YcANl4oNHBa5V-4KS2cGA"
            >
                <GoogleMap
                    center={center}
                    zoom={zoom}
                    mapContainerStyle={containerStyle}
                    onClick={(e)=> drawMarker ? addMarker(e.latLng.toJSON()) : null}



                //     <Marker
                    //     position = {{ lat: args[0].latLng.lat(), lng: args[0].latLng.lng()} }
                    // />)
                            // console.log({lat: args[0].latLng.lat(), lng:  args[0].latLng.lng()})
                >
                  <PolygonMkad/>
                    {markers ? (
                        markers.map((marker) => {
                            return (
                                <Marker
                                    key={marker.id}
                                    draggable={drawMarker}
                                    position={marker.coords}
                                    onDragEnd={e => marker.coords = e.latLng.toJSON()}
                                />
                            )
                        })
                    ) : null }
                </GoogleMap>
            </LoadScript>
        </div>

    )
}

export default React.memo(MyMapComponent)
