import React from 'react'
import {useState, useCallback} from 'react'

import paths from "../../mkad";
import './map.scss'

import
    {GoogleMap,
    Marker,
    useLoadScript,
    DirectionsRenderer,
    DirectionsService,
    Polyline,
    InfoWindow,
    DistanceMatrixService}
from '@react-google-maps/api';

import PolygonMkad from "../PolygonMkad/Polygon";



const containerStyle = {
    width: '100wh',
    height: '100vh'
}


const  Map = () => {

    const { isLoaded } = useLoadScript({
        googleMapsApiKey: "AIzaSyBYEey3dAT7X6YcANl4oNHBa5V-4KS2cGA",
    });

    const [ myMap, setMyMap ] = useState(null);
    const [center, setCenter] = useState({lat: 55.7522200, lng: 37.6155600});
    const [zoom,setZoom] = useState(8);
    const [ markers, setMarkers ] = useState([]);
    const [coordinates, setCoordinates] = useState(null)
    const [response, setResponse] = useState(null)
    const [travelMode, setTravelMode] = useState('DRIVING')
    const [origin, setOrigin] = useState('')
    const [destination, setDestination] = useState(null)

    const optionsPolyline = {
        strokeColor: 'green',
        strokeOpacity: 1,
        strokeWeight: 5,
        fillColor: 'green',
        fillOpacity: 1,
        visible: true,
    }




    const onMapClick =  useCallback((event) => {
            setCoordinates(() => event.latLng.toJSON());
            if (origin !== '' ) {
                setOrigin(() => event.latLng.toJSON())
            }

                setDestination(() => resultCoordinates)


            }, [])





    const  distance =  (lat1, lng1, lat2, lng2) =>  {
        if (coordinates !== null) {
            if ((lat1 === lat2) && (lng1 === lng2)) {
                return 0;
            }
            else {
                let radlat1 = Math.PI * lat1/180;
                let radlat2 = Math.PI * lat2/180;
                let theta = lng1-lng2;
                let radtheta = Math.PI * theta/180;

                let dist = Math.sin(radlat1) * Math.sin(radlat2) + Math.cos(radlat1) * Math.cos(radlat2) * Math.cos(radtheta);
                if (dist > 1) {
                    dist = 1;
                }
                dist = Math.acos(dist);
                dist = dist * 180/Math.PI;
                dist = dist * 60 * 1.1515 *1.609344 * 1000

                return dist;
            }
        }
    }



    const resultCoordinates =  (coordinates) => {

        if (coordinates !== null) {
            const lat1 = coordinates.lat
            const lng1 = coordinates.lng
            const resultDistance =  paths.map(item => distance(lat1, lng1, item.lat, item.lng));
            const resultSort = resultDistance.slice(0).sort((a, b) => a - b );
            const resultIndex = resultDistance.indexOf(resultSort[0]);



            return paths[resultIndex]
        }

    }





    const path = [coordinates,destination]


    const directionsServiceOptions = {
        origin: coordinates,
        destination: destination,
        travelMode: travelMode,
    }



    const directionsCallback = useCallback((res) => {
        if (res !== null) {
            if (res.status === 'OK') {
                setResponse(res)
            } else {
                console.log('response: ', res)
            }
        }
    }, [])


    const renderMap = () => (
        <div>
            <GoogleMap
                center={center}
                zoom={zoom}
                mapContainerStyle={containerStyle}
                onLoad={map => setMyMap(map)}
                onClick={onMapClick}
            >
                <PolygonMkad/>
               {markers ? (<Marker position={coordinates}/>) : null}
                    <DirectionsService
                        options={directionsServiceOptions}
                        callback={directionsCallback}
                    />
                {(response !== '') ? (
                    <DirectionsRenderer directions ={response} />
                ): null}

                {response ? (     <Polyline
                        options = {optionsPolyline}
                        path = {path}
                    />
                ): null}

                {(coordinates !== null  && response) ? (<InfoWindow
                    position = {{lat: 55.7522200, lng: 37.6155600}}
                    >

                    <div>{response.routes[0].legs[0].start_address}</div>
                    </InfoWindow>): null}

            </GoogleMap>


        </div>
)

    return isLoaded ? renderMap() : null;

}



export default React.memo(Map)
