import React from 'react'
import {useState, useCallback} from 'react'

import './map.scss'

import {GoogleMap, Marker, useLoadScript, DirectionsRenderer, DirectionsService} from '@react-google-maps/api';

import PolygonMkad from "../PolygonMkad/Polygon";


const containerStyle = {
    Width: '100wh',
    height: '100vh'
}


const  Map = () => {

    const [ myMap, setMyMap ] = useState(null);
    const [center, setCenter] = useState({lat: 55.7522200, lng: 37.6155600});
    const [zoom,setZoom] = useState(10);
    const [ markers, setMarkers ] = useState([]);
    const [coordinates, setCoordinates] = useState(null)


    const { isLoaded } = useLoadScript({
        googleMapsApiKey: "AIzaSyBYEey3dAT7X6YcANl4oNHBa5V-4KS2cGA",
    });

    // const onMapClick = useCallback((event) => {
    //         setCoordinates(coordinates => event.latLng.toJSON());
    //         setMarkers(markers => (
    //                 <Marker position={coordinates}/>
    //             )
    //         )
    // }, [])



    const [response, setResponse] = React.useState(null)

    const directionsServiceOptions = {
        origin: coordinates,
        destination: center,
        travelMode: 'DRIVING'
    }

    const directionsCallback = React.useCallback((res) => {
        if (res !== null) {
            if (res.status === 'OK') {
                setResponse(res)
            } else {
                console.log('response: ', res)
            }
        }
    }, [])



    const renderMap = () => (
        <div>
            <GoogleMap
                center={center}
                zoom={zoom}
                mapContainerStyle={containerStyle}
                onLoad={map => setMyMap(map)}
                onClick={async (event) => {
                  setCoordinates(coordinates => event.latLng.toJSON());
                    await setMarkers(markers => (
                            <Marker position={coordinates}/>
                        )
                    )

                }}
            >
                <PolygonMkad/>
               {markers}

              <DirectionsService
                  options={directionsServiceOptions}
                  callback={directionsCallback}
              />

              <DirectionsRenderer
                // directions = {directionsRendererOptions}
              />


            </GoogleMap>
        </div>
)

    return isLoaded ? renderMap() : null;

}



export default React.memo(Map)
