import React from 'react'
import {useState} from 'react'



import './map.scss'

import {GoogleMap, LoadScript, Marker} from '@react-google-maps/api';
import PolygonMkad from "../PolygonMkad/Polygon";

const containerStyle = {
    minWidth: '100vh',
    height: '100vh'
}





function MyMapComponent() {

    const [center, setCenter] = useState({ lat: 55.7522200, lng: 37.6155600, });
    const [zoom,setZoom] = useState(10);

    const [marker, setMarker] = useState(null)





    return  (
        <div className='map'>
            <LoadScript
                googleMapsApiKey="AIzaSyBYEey3dAT7X6YcANl4oNHBa5V-4KS2cGA"
            >
                <GoogleMap
                    center={center}
                    zoom={zoom}
                    mapContainerStyle={containerStyle}
                    onClick = {(...args) => setMarker(marker => {
                            marker =  <Marker
                                position = {{lat: args[0].latLng.lat(), lng:  args[0].latLng.lng()}}
                            />
                    }

                    )}
                >
                  <PolygonMkad/>


                </GoogleMap>
            </LoadScript>
        </div>

    )
}

export default React.memo(MyMapComponent)
