import React from 'react'
import {useState} from 'react'



import './map.scss'

import {GoogleMap, LoadScript, Marker, useGoogleMap, useLoadScript} from '@react-google-maps/api';
import PolygonMkad from "../PolygonMkad/Polygon";

const containerStyle = {
    minWidth: '100vh',
    height: '100vh'
}




function MyMapComponent() {

    const [center, setCenter] = useState({ lat: 55.7522200, lng: 37.6155600, });
    const [zoom,setZoom] = useState(10);

    const [marker,setMarker] = useState()

    const { isLoaded } = useLoadScript({
        googleMapsApiKey: "AIzaSyBYEey3dAT7X6YcANl4oNHBa5V-4KS2cGA",
    });

    return  (
        <div className='map'>
            <LoadScript
                googleMapsApiKey="AIzaSyBYEey3dAT7X6YcANl4oNHBa5V-4KS2cGA"
            >
                <GoogleMap
                    center={center}
                    zoom={zoom}
                    mapContainerStyle={containerStyle}
                    onClick = {(...args) => {setMarker(<Marker
                        position = {{ lat: args[0].latLng.lat(), lng: args[0].latLng.lng()} }
                    />)
                    }}
                            // console.log({lat: args[0].latLng.lat(), lng:  args[0].latLng.lng()})
                >
                  <PolygonMkad/>
                  <Marker
                      position = {{ lat: 55.7522200, lng: 37.6155600, }}
                  />
                </GoogleMap>
            </LoadScript>
        </div>

    )
}

export default React.memo(MyMapComponent)
