import React from 'react'
import {useState, useCallback} from 'react'

import './map.scss'

import {GoogleMap, Marker, useLoadScript} from '@react-google-maps/api';

import PolygonMkad from "../PolygonMkad/Polygon";
import DirectionsRender from "../DirectionsRender/DirectionsRender";

const containerStyle = {
    Width: '100wh',
    height: '100vh'
}


const  Map = () => {

    const [ myMap, setMyMap ] = useState(null);
    const [center, setCenter] = useState({lat: 55.7522200, lng: 37.6155600});
    const [zoom,setZoom] = useState(10);

    const { isLoaded } = useLoadScript({
        googleMapsApiKey: "AIzaSyBYEey3dAT7X6YcANl4oNHBa5V-4KS2cGA",
    });

    const onMapClick = useCallback((event) => {
            coordinates = event.latLng.toJSON()
            setMarkers(markers => (
                    <Marker position={coordinates}/>
                )


            )
    }, [])

    const [ markers, setMarkers ] = useState([]);

    let  coordinates
    console.log(coordinates)

    const renderMap = () => (
        <div>
            <GoogleMap
                center={center}
                zoom={zoom}
                mapContainerStyle={containerStyle}
                onLoad={map => setMyMap(map)}
                onClick={onMapClick}
            >
                <PolygonMkad/>
               {markers}
                <DirectionsRender
                    center={center}
                    coordinates ={coordinates}
                />
            </GoogleMap>
        </div>
)

    return isLoaded ? renderMap() : null;

}



export default React.memo(Map)
