import React from 'react'
import {useState, useEffect} from 'react'



import './map.scss'

import {GoogleMap, LoadScript, Marker, useGoogleMap, useLoadScript} from '@react-google-maps/api';
import PolygonMkad from "../PolygonMkad/Polygon";

const containerStyle = {
    minWidth: '100vh',
    height: '100vh'
}

const google = window.google

function MyMapComponent() {

    const [ myMap, setMyMap ] = useState(null);
    const [center, setCenter] = useState({lat: 55.7522200, lng: 37.6155600});
    const [zoom,setZoom] = useState(10);

    const { isLoaded } = useLoadScript({
        googleMapsApiKey: "AIzaSyBYEey3dAT7X6YcANl4oNHBa5V-4KS2cGA",
    });

    const [ id, setId ] = useState(0);
    const [ markers, setMarkers ] = useState([]);



    const renderMap = () => (
        <div>
            <GoogleMap
                center={center}
                zoom={zoom}
                mapContainerStyle={containerStyle}
                onLoad={map => setMyMap(map)}
                onClick={(e)=> {

                 }
                }
            >
                <PolygonMkad/>
                {markers ? (
                    markers.map((marker) => {
                        return (
                            <Marker
                                key={marker.id}
                                position={marker.coords}
                                onDragEnd={e => marker.coords = e.latLng.toJSON()}
                            />
                        )
                    })
                ) : null }
            </GoogleMap>
        </div>
)




    return isLoaded ? renderMap() : null;

}



export default MyMapComponent
